package com.example.rabbitmqnonblocking.services;

import java.util.Arrays;
import java.util.Map;
import java.util.concurrent.Callable;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Future;
import java.util.concurrent.ScheduledExecutorService;
import java.util.concurrent.TimeUnit;

import org.springframework.amqp.rabbit.core.RabbitTemplate;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import com.example.rabbitmqnonblocking.rabbitconfig.Queues;

@Service
public class PublishService {
    private static final int N = 100;
    private static int reqCount = 0;
    ExecutorService service = Executors.newFixedThreadPool(N);
    ScheduledExecutorService canceller = Executors.newSingleThreadScheduledExecutor();
    private static final ConcurrentHashMap<String, Integer> countMap = new ConcurrentHashMap<>();

    public <T> Future<T> executeTask(Callable<T> c, long timeoutMS) {
        final Future<T> future = service.submit(c);
        canceller.schedule((Callable<Void>) () -> {
            if (!future.isDone()) {
                System.out.println("cancelling publish to rabbitmq, time limit exceeded");
                countMap.put("timed-out", countMap.getOrDefault("timed-out", 0) + 1);
            }
            future.cancel(true);
            reqCount++;
            System.out.println(reqCount);
            if (reqCount % 100 == 0) {
                System.out.println("\n\n\n");
                for (Map.Entry<String, Integer> entry : countMap.entrySet()) {
                    System.out.println(entry.getKey() + ": " + entry.getValue());
                }
                System.out.println("\n\n\n");
            }
            return null;
        }, timeoutMS, TimeUnit.MILLISECONDS);
        return future;
    }

    private final RabbitTemplate rabbitTemplate;

    @Autowired
    public PublishService(RabbitTemplate rabbitTemplate) {
        this.rabbitTemplate = rabbitTemplate;
    }

    public void publish() {
        executeTask(() -> {
            try {
                System.out.println("starting publish...");
                rabbitTemplate.convertAndSend(Queues.NON_BLOCKING_TEST, "");
                System.out.println("publish completed.");
                countMap.put("published", countMap.getOrDefault("published", 0) + 1);
            } catch (Exception e) {
                System.out.println("exception thrown" + e.getMessage() + Arrays.toString(e.getStackTrace()));
                throw e;
            }
            return null;
        }, 1000);
    }
}
